import { Component, OnInit } from "@angular/core";
import { AddRenterService } from "./add-renter-service";
import { MatDialogRef } from "@angular/material/dialog";
import { environment } from "src/environments/environment";
import { HttpClient } from "@angular/common/http";
import { IDropdownSettings } from "ng-multiselect-dropdown";
import { SendEmailDialogService } from "../send-email-dialog/send-email-dialog.service";
import { FormControl, FormGroup } from "@angular/forms";
import { Observable } from "rxjs";
import { map, startWith } from "rxjs/operators";
// import { ToastrService } from "ngx-toastr";
import { THIS_EXPR } from "@angular/compiler/src/output/output_ast";

@Component({
  selector: "app-add-renter-dialog",
  templateUrl: "./add-renter-dialog.component.html",
  styleUrls: ["./add-renter-dialog.component.css"],
})
export class AddRenterDialogComponent implements OnInit {
  InviteRenterObj: any = {};
  InviteRenterObj2: any = {};
  userID: any;
  kohabID: any;
  msg: string = "";
  addRenterSuccessMessage: string = "";
  addRenterSuccessMessageCon: boolean = false;
  addRenterFailureMessageCon: boolean = false;
  isEnable: boolean = false;
  filtered_members: string[] | any;
  dropdownList: any[] = [];
  selectedItems: any[] = [];
  dropdownSettings: IDropdownSettings = {};
  countryCode: any;
  hoaMemberName: any;
  hoaMemberId: any;
  homeowner_role: boolean | undefined;
  hoaMemberIdname: any;
  control = new FormControl();
  filteredStreets: Observable<string[]> | any;
  filteredOptions: any;
  formGroup: FormGroup | undefined;
  names: any;
  datamembers: any;
  kohabId: any;
  kohabData: any;

  constructor(
    private inviteAddRenterService: AddRenterService,
    private http: HttpClient,
    private ss: SendEmailDialogService,
    public dialogRef: MatDialogRef<AddRenterDialogComponent>,
    // private toastr: ToastrService,
  ) { }

  renterinvitedata = this.inviteAddRenterService.getrenterData();

  ngOnInit(): void {
    const user = JSON.parse(localStorage.getItem("user") || "{}");
    //console.log(user)
    if (user.role == "homeowner") {
      this.homeowner_role = true;
      this.hoaMemberIdname = user.userId;
      this.hoaMemberName = user.firstName + " " + user.lastName;
      this.kohabId = user.kohabId
    } else {
      if (user.role != "developer") {
        this.kohabId = user.kohabId;
      } else {
        this.kohabData = JSON.parse(localStorage.getItem("kohab") || "{}");
        this.kohabId = this.kohabData.kohabId;
      }
      this.hoaMemberName = user.firstName + " " + user.lastName;
      this.inviteAddRenterService
        .getKohabUsers(this.kohabId)
        .subscribe((data: any) => {
          this.filtered_members = data.users.filter(function (data2: any) {
            return data2.role != 'renter' && data2.role != 'propertymanager' && data2.role != 'developer'
          });
          this.datamembers = this.filtered_members;
          this.filtered_members = this.filtered_members.map(
            (data: any) =>
              data.firstName + " " + data.lastName + "(" + data.phone + ")"
          );
          this.filtered_members_data();
        });
      this.dropdownSettings = {
        singleSelection: true,
        idField: "userId",
        textField: "emailId",
        selectAllText: "Select All",
        unSelectAllText: "UnSelect All",
        itemsShowLimit: 1,
        allowSearchFilter: true,
        searchPlaceholderText: "Search HOA Members",
        closeDropDownOnSelection: true,
      };
    }
    this.getCountryCode();
  }

  onItemSelect(item: any) {
    //console.log(item);
  }

  onItemDeSelect(item: any) {
    //console.log(item);
  }

  fetchMore(item: any) {
    //console.log(item);
  }

  getCountryCode() {
    this.http
      .get("https://api.ipify.org/", { responseType: "text" as "json" })
      .subscribe((responseip: any) => {
        if (responseip) {
          this.http
            .get(
              `${environment.url_address}` +
              `${environment.countrycode}` +
              "/" +
              `${responseip}`
            )
            .subscribe((responsecountrycode: any) => {
              if (
                responsecountrycode.sucess &&
                "countryCode" in responsecountrycode
              ) {
                this.countryCode = "+" + responsecountrycode.countryCode;
              }
            });
        }
      });
  }

  filtered_members_data() {
    this.filteredStreets = this.control.valueChanges.pipe(
      startWith(""),
      map((value) => this._filter(value))
    );
  }

  private _filter(value: string): string[] {
    const filterValue = value.toLowerCase();
    return this.filtered_members.filter((option: string) =>
      option.toLowerCase().includes(filterValue)
    );
  }

  onSubmit(data1: any) {
    //console.log(this.homeowner_role);
    if (this.homeowner_role) {
      if (data1.valid) {
        let localData = JSON.parse(localStorage.getItem("user") || "{}");
        this.InviteRenterObj.userId = localData.userId;
        this.InviteRenterObj.unitNumber = localData.unitNumber
        this.InviteRenterObj.kohabId = this.kohabId;
        this.InviteRenterObj.countryCode = this.countryCode;
        if (this.homeowner_role) {
          this.InviteRenterObj.hoaMemberId = this.hoaMemberIdname;
        }
        //console.log(this.InviteRenterObj);
        this.inviteAddRenterService
          .inviteRenter(this.InviteRenterObj)
          .subscribe((data) => {
            if (data.success) {
              this.addRenterFailureMessageCon = false;
              this.addRenterSuccessMessageCon = true;
              this.addRenterSuccessMessage = data.message;
              this.msg="";
              // this.toastr.success(data.message);
              this.isEnable = true;
              setTimeout(() => {
                this.dialogRef.close();
              }, 3000);
              // this.dialogRef.close();
            } else {
              this.addRenterSuccessMessageCon = false;
              this.addRenterFailureMessageCon = true;
              this.msg = data.message;
              this.isEnable = false;
              // this.toastr.error(data.message);
            }
          });
      } else {
        this.addRenterSuccessMessageCon = false;
        this.addRenterFailureMessageCon = true;
        this.msg = "Please fill the fields ";
        this.isEnable = false;
        // this.toastr.error("Please fill all the fields");
      }
    } else {
      if (data1.valid && this.InviteRenterObj2.hoaMemberId != undefined) {
        const InviteRe = this.InviteRenterObj2.hoaMemberId.split("(");
        const InviteRe2 = InviteRe[1].split(")");
        const result = this.datamembers.find(
          (value: { phone: any }) => value.phone === InviteRe2[0]
        );
        if (result == undefined) {
          this.isEnable = false;
          this.addRenterSuccessMessageCon = false;
          this.addRenterFailureMessageCon = true;
          this.msg = "please fill the correct hoa details";
        } else {
          let localData = JSON.parse(localStorage.getItem("user") || "{}");
          this.InviteRenterObj.userId = localData.userId;
          this.InviteRenterObj.unitNumber = localData.unitNumber
          this.InviteRenterObj.kohabId = this.kohabId;
          this.InviteRenterObj.hoaMemberId = result.userId;
          this.InviteRenterObj.countryCode = this.countryCode;
          this.inviteAddRenterService
            .inviteRenter(this.InviteRenterObj)
            .subscribe((data) => {
              //console.log(data)
              if (data.success) {
                this.addRenterFailureMessageCon = false;
                this.addRenterSuccessMessageCon = true;
                this.addRenterSuccessMessage = data.message;
                this.isEnable = true;
                this.msg ="";
                // this.toastr.success(data.message);
                setTimeout(() => {
                  this.dialogRef.close();
                }, 3000);
                // this.dialogRef.close();
              }
              else {
                this.addRenterSuccessMessageCon = false;
                this.addRenterFailureMessageCon = true;
                this.msg = data.message;
                this.isEnable = false;
                // this.toastr.error(data.message);
              }
            });
        }
      } else {
        this.addRenterSuccessMessageCon = false;
        this.addRenterFailureMessageCon = true;
        this.msg = "Please fill the fields";
        this.isEnable = false;
        // this.toastr.error("Please fill all the fields");
      }
    }
  }
}
<div id="card-div">
  <form #userPost="ngForm" (ngSubmit)="onSubmit(userPost)">
    <div class="row" id="header">
      <div class="col-sm-6">
        <strong style="margin-top: 0%; margin-left: 2%">Add Renter</strong>
      </div>
      <div class="col-sm-6">
        <mat-icon
          mat-dialog-close
          style="float: right; margin-right: 2%; margin-top: 0%"
          id="iconclose"
          >close</mat-icon
        >
      </div>
    </div>

    <div class="content">
      <div class="form-group row">
        <label>First Name </label>
        <input
          type="text"
          class="form-control"
          id="input"
          name="firstName"
          [(ngModel)]="InviteRenterObj.firstName"
          #firstName="ngModel"
          required
          pattern="[a-zA-Z ]+"
        />
      </div>
      <div
        *ngIf="firstName.invalid && (firstName.dirty || firstName.touched)"
        class="error"
        style="position: absolute"
      >
        <small *ngIf="firstName.errors?.required">
          First name is required.
        </small>
        <small *ngIf="firstName.errors?.pattern">
          First name contain only letters
        </small>
      </div>

      <div class="form-group row">
        <label id="lable1">Last Name</label>
        <input
          type="text"
          class="form-control"
          id="input"
          name="lastName"
          [(ngModel)]="InviteRenterObj.lastName"
          #lastName="ngModel"
          required
          pattern="[a-zA-Z ]+"
        />
      </div>
      <div
        *ngIf="lastName.invalid && (lastName.dirty || lastName.touched)"
        class="error"
        style="position: absolute"
      >
        <small *ngIf="lastName.errors?.required">
          Last name is required.
        </small>
        <small *ngIf="lastName.errors?.pattern">
          Last name contain only letters
        </small>
      </div>
      <div class="form-group row">
        <label id="lable1">Email</label>
        <input
          type="text"
          class="form-control"
          id="input"
          name="email"
          [(ngModel)]="InviteRenterObj.email"
          #email="ngModel"
          required
          pattern="^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$"
        />
      </div>
      <div
        *ngIf="email.invalid && (email.dirty || email.touched)"
        class="error"
        style="position: absolute"
      >
        <small *ngIf="email.errors?.required"> Email is required. </small>
        <small *ngIf="email.errors?.pattern">
          Only email pattern is allowed
        </small>
      </div>
      <div class="form-group row">
        <label id="lable1">Phone</label>
        <input
          type="text"
          class="form-control"
          id="input"
          name="phone"
          [(ngModel)]="InviteRenterObj.phone"
          mask="(000) 000-0000"
          pattern="^\d{10}$"
          required
          #phone="ngModel"
        />
      </div>
      <div
        *ngIf="phone.invalid && (phone.dirty || phone.touched)"
        class="error"
        style="position: absolute"
      >
        <small *ngIf="phone.errors?.required">
          Phone number is required.
        </small>
        <small *ngIf="phone.errors?.pattern">
          Only 10 digits are allowed
        </small>
      </div>

      <div class="form-group row" *ngIf="homeowner_role; else notHomeowner">
        <label id="lable1">HOA Member</label>
        <input
          type="text"
          class="form-control"
          id="input"
          name="hoaMemberName"
          [ngModel]="hoaMemberName"
          readonly
          *ngIf="homeowner_role"
        />
      </div>
      <ng-template #notHomeowner>
        <div class="form-group row">
          <label id="lable1">HOA Member</label>
          <input
            type="text"
            class="form-control"
            id="input"
            name="hoaMemberId"
            [(ngModel)]="InviteRenterObj2.hoaMemberId"
            placeholder="Search HOA Members"
            [matAutocomplete]="auto"
            [formControl]="control"
            required
          />
          <mat-autocomplete #auto="matAutocomplete">
            <mat-option
              *ngFor="let item of filteredStreets | async"
              [value]="item"
            >
              {{ item }}
            </mat-option>
          </mat-autocomplete>
        </div>
      </ng-template>
      <div
        *ngIf="hoaMemberName.touched"
        class="error"
        style="position: absolute"
      >
        <small *ngIf="hoaMemberName.errors?.required">
          Hoa Member is required.
        </small>
      </div>
    </div>
    <p
      *ngIf="addRenterSuccessMessageCon"
      id="successMsg"
    >
      {{ addRenterSuccessMessage }}
    </p>
    <div>
      <p
        *ngIf="addRenterFailureMessageCon"
        id="failureMsg"
      >
        {{ msg }}
      </p>
    </div>
    <div *ngIf="isEnable; else notEnable" class="content">
      <button id="button" style="opacity: 0.8" [disabled]="true" type="submit">
        Add
      </button>
    </div>
    <ng-template #notEnable class="content">
      <button id="button" type="submit">Add</button>
    </ng-template>
  </form>
</div>
